package ejemplodowhile2;

public class Principal {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Do...While
		int condicion = 0, tope = 6;
		do {
			System.out.println("Carlos Durbán");
			condicion++;//Se usa siempre "++" para ir añadiendo de 1 en 1 a un contador.
		}while(condicion < tope);

	}

}
